import React, { useEffect, useState } from 'react'
import { useParams, useLocation } from 'react-router-dom';
import styled from 'styled-components';


const Title = styled.h1`
  font-size: 48px;
  color: ${(props) => props.theme.accentColor};
`;

const Loader = styled.span`
  text-align: center;
  display: block;
`;

const Container = styled.div`
  padding: 0px 20px;
  max-width: 480px;
  margin: 0 auto;
`;

const Header = styled.header`
  height: 15vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

interface IParams {
    stockId: string
}
interface ILocation {
    name: string
}

function Stock() {
    const [loading, setLoading] = useState(true)
    const { stockId } = useParams<IParams>()
    const { state } = useLocation<ILocation>()
    const [info, setInfo] = useState({})
    useEffect(() => {
        (async () => {
            const response = await (await fetch(`https://api.odcloud.kr/api/GetStockSecuritiesInfoService/v1/getStockPriceInfo?resultType=json&serviceKey=GU%2FPw6QXvxX8rdXGsY9vDQWG3v8mr8imsKIAHd11KmEe0GOfu5NFoK4bv96cfhkwLKdy85VXG1mtXprIuY2HAw%3D%3D&numOfRows=20&pageNo=1&&isinCd=${stockId}`)).json();

            console.log(response.body.items.item);
            setInfo(response.body.items.item);
            setLoading(false);
        })();
    }, [])

    return (
        <Container>
            <Header>
                <Title>{state?.name|| "Loading..."}</Title>
            </Header>
            {loading ? <Loader></Loader> : {state} }
        </Container>
    )
}

export default Stock
