import ApexCharts from 'apexcharts'
import React from 'react'
import { useQuery } from "react-query"
import { fetchStockHistory } from '../api'

interface IHistorical {
    time_open: string;
    time_close: string;
    open: number;
    high: number;
    low: number;
    close: number;
    volume: number;
    market_cap: number;
  }
interface IChart {
    stockId: string;
}

function Chart({ stockId }: IChart) {
    const { isLoading, data } = useQuery<IHistorical>(["history", stockId], ()=> fetchStockHistory(stockId))
    return (
        <div>
            {isLoading ? (
                "Loading chart..."
            ) : (
                <ApexChart
                type="line"
                series={[
                    {
                    name: "Price",
                    data: data?.map((price) => price.close),
                    },
                ]}
                options={{
                    theme: {
                    mode: "dark",
                    },
                    chart: {
                    height: 300,
                    width: 500,
                    toolbar: {
                        show: false,
                    },
                    background: "transparent",
                    },
                    grid: { show: false },
                    stroke: {
                    curve: "smooth",
                    width: 4,
                    },
                    yaxis: {
                    show: false,
                    },
                    xaxis: {
                    axisBorder: { show: false },
                    axisTicks: { show: false },
                    labels: { show: false },
                    },
                }}
                />
            )}
        </div>
    )
}

export default Chart
