import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components'

const Container = styled.div`
  padding: 0px 20px;
  max-width: 480px;
  margin: 0 auto;
`;

const Header = styled.header`
  height: 15vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StockList = styled.ul``;

const Stock = styled.li`
  background-color: white;
  color: ${(props) => props.theme.bgColor};
  border-radius: 15px;
  margin-bottom: 10px;
  a {
    padding: 20px;
    transition: color 0.2s ease-in;
    display: flex;
    align-items: center;
    justify-content: space-between;
  }
  &:hover {
    a {
      color: ${(props) => props.theme.accentColor};
    }
  }
`;

const Title = styled.h1`
    font-size: 48px;
    color: ${(props) => props.theme.accentColor};
`;

const Loader = styled.span`
  text-align: center;
  display: block;
`;

const UpStock = styled.span`
  color: ${props => props.theme.upColor};
`
const DownStock = styled.span`
  color: ${props => props.theme.downColor};
`

interface IStocks {
    basDt: string;
    srtnCd: string;
    isinCd: string;
    itmsNm: string;
    mrktCtg: string;
    clpr: number;
    vs: number;
    fltRt: number;
    mkp: number;
    hipr: number;
    lopr: number;
    trqu: number;
    trPrc: number;
    lstgStCnt: number;
    mrktTotAmt: number;
}

function Stocks() {
    const [stocks, setStocks] = useState<IStocks[]>([]);
    const [loading, setLoading] = useState(true);
    useEffect(() => {
        (async () => {
            const response = await fetch("https://api.odcloud.kr/api/GetStockSecuritiesInfoService/v1/getStockPriceInfo?resultType=json&serviceKey=GU%2FPw6QXvxX8rdXGsY9vDQWG3v8mr8imsKIAHd11KmEe0GOfu5NFoK4bv96cfhkwLKdy85VXG1mtXprIuY2HAw%3D%3D&numOfRows=20&pageNo=1");
            const json = await response.json();

            setStocks(json.response.body.items.item);
            setLoading(false);
        })();
    }, [])
    return (
        <Container>
            <Header>
                <Title>주시</Title>
            </Header>
            {loading ? (
                <Loader>Load~~~</Loader>
            ) : (
                <StockList>
                     {stocks.map((stock) => (
                        <Stock key={stock.isinCd}>
                            <Link to={{
                                pathname: `/${stock.isinCd}`,
                                state: { name: stock.itmsNm },
                                }}>
                                {stock.itmsNm} 
                                {stock.vs < 0 ? <DownStock>{stock.vs}</DownStock> : <UpStock>{stock.vs}</UpStock>}
                            </Link>
                        </Stock>
                    ))} 
                </StockList>
            )}
        </Container>
    )
}

export default Stocks
