import React, { useEffect, useState } from 'react'
import styled from 'styled-components'

const Container = styled.div`
  padding: 0px 20px;
  max-width: 480px;
  margin: 0 auto;
`;

const Header = styled.header`
  height: 15vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StockList = styled.ul``;

const Stock = styled.li`
  background-color: white;
  color: ${(props) => props.theme.bgColor};
  border-radius: 15px;
  margin-bottom: 10px;
  a {
    padding: 20px;
    transition: color 0.2s ease-in;
    display: block;
  }
  &:hover {
    a {
      color: ${(props) => props.theme.accentColor};
    }
  }
`;

const Title = styled.h1`
    font-size: 48px;
    color: ${props => props.theme.accentColor}
`;

const Loader = styled.span`
  text-align: center;
  display: block;
`;

interface IStocks {
  id: string;
  name: string;
  symbol: string;
  rank: number;
  is_new: boolean;
  is_active: boolean;
  type: string;
}

function Stocks() {
    const [stocks, setStocks] = useState<IStocks[]>();
    const [loading, setLoading] = useState(true);
    useEffect(() => {
        (async () => {
            const response = await fetch("https://api.odcloud.kr/api/GetStockSecuritiesInfoService/v1/getStockPriceInfo?resultType=json&serviceKey=GU%2FPw6QXvxX8rdXGsY9vDQWG3v8mr8imsKIAHd11KmEe0GOfu5NFoK4bv96cfhkwLKdy85VXG1mtXprIuY2HAw%3D%3D");
            const json = await response.json();
            setStocks(json.slice(0, 100));
            setLoading(false);
        })()
    }, [])
    return (
        <Container>
            <Header>
                <Title>주시</Title>
            </Header>
            <StockList>
            {}
            </StockList>
        </Container>
    )
}

export default Stocks
