import React, { useEffect, useState } from 'react'
import { Switch, Route, useParams, useLocation } from 'react-router-dom';
import styled from 'styled-components';


const Title = styled.h1`
  font-size: 48px;
  color: ${(props) => props.theme.accentColor};
`;

const Loader = styled.span`
  text-align: center;
  display: block;
`;

const Container = styled.div`
  padding: 0px 20px;
  max-width: 480px;
  margin: 0 auto;
`;

const Header = styled.header`
  height: 15vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Overview = styled.div`
  display: flex;
  justify-content: space-between;
  background-color: rgba(0, 0, 0, 0.5);
  padding: 10px 20px;
  border-radius: 10px;
`;
const OverviewItem = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  span:first-child {
    font-size: 10px;
    font-weight: 400;
    text-transform: uppercase;
    margin-bottom: 5px;
  }
`;
const Description = styled.p`
  margin: 20px 0px;
`;

interface IParams {
    stockId: string;
}
interface ILocation {
    name: string;
}
interface IData {
    basDt: string;
    clpr: string;
    fltRt: string;
    hipr: string;
    isinCd: string;
    itmsNm: string;
    lopr: string;
    lstgStCnt: string;
    mkp: string;
    mrktCtg: string;
    mrktTotAmt: string;
    srtnCd: string;
    trPrc: string;
    trqu: string;
    vs: string;
}

function Stock() {
    const [loading, setLoading] = useState(true)
    const { stockId } = useParams<IParams>()
    const { state } = useLocation<ILocation>()
    const [info, setInfo] = useState<IData>()
    useEffect(() => {
        (async () => {
            const response = await (await fetch(`https://api.odcloud.kr/api/GetStockSecuritiesInfoService/v1/getStockPriceInfo?resultType=json&serviceKey=GU%2FPw6QXvxX8rdXGsY9vDQWG3v8mr8imsKIAHd11KmEe0GOfu5NFoK4bv96cfhkwLKdy85VXG1mtXprIuY2HAw%3D%3D&numOfRows=20&pageNo=1&isinCd=${stockId}`)).json();

            console.log(response.response.body.items.item);
            setInfo(response.response.body.items.item[0]);
            setLoading(false);
        })();
    }, [stockId])

    return (
        <Container>
            <Header>
                <Title>{state?.name ? state.name : loading ? "Loading..." : info?.itmsNm}</Title>
            </Header>
            {loading ? (
                <Loader>Loading...</Loader>
            ) : (
                <>
                <Overview>
                    <OverviewItem>
                    <span>주식의 시장 구분:</span>
                    <span>{info?.mrktCtg}</span>
                    </OverviewItem>
                    <OverviewItem>
                    <span>하루 중 가격의 최고치:</span>
                    <span>{info?.hipr}</span>
                    </OverviewItem>
                    <OverviewItem>
                    <span>하루 중 가격의 최저치:</span>
                    <span>{info?.lopr}</span>
                    </OverviewItem>
                </Overview>
                <Description>{info?.vs}</Description>
                <Overview>
                    <OverviewItem>
                    <span>체결수량의 누적 합계:</span>
                    <span>{info?.trqu}</span>
                    </OverviewItem>
                    <OverviewItem>
                    <span>규시장의 매매시간종료시까지 형성되는 최종가격:</span>
                    <span>전일 대비 등락: {info?.clpr}</span>
                    </OverviewItem>
                </Overview>
                <Switch>
                    <Route path={`/${stockId}/price`}>
                    <></>
                    </Route>
                    <Route path={`/${stockId}/chart`}>
                    <></>
                    </Route>
                </Switch>
                </>
            )}
        </Container>
    )
}

export default Stock
