import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom';
import styled from 'styled-components'
import RssParser from 'rss-parser'

const Container = styled.div`
  padding: 0px 20px;
  max-width: 480px;
  margin: 0 auto;
`;

const Header = styled.header`
  height: 15vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const StockList = styled.ul``;

const Stock = styled.li`
  background-color: white;
  color: ${(props) => props.theme.bgColor};
  border-radius: 15px;
  margin-bottom: 10px;
  a {
    padding: 20px;
    transition: color 0.2s ease-in;
    display: block;
  }
  &:hover {
    a {
      color: ${(props) => props.theme.accentColor};
    }
  }
`;

const Title = styled.h1`
    font-size: 48px;
    color: ${(props) => props.theme.accentColor};
`;

const Loader = styled.span`
  text-align: center;
  display: block;
`;

interface IStocks {
    basDt: string;
    srtnCd: string;
    isinCd: string;
    itmsNm: string;
    mrktCtg: string;
    clpr: number;
    vs: number;
    fltRt: number;
    mkp: number;
    hipr: number;
    lopr: number;
    trqu: number;
    trPrc: number;
    lstgStCnt: number;
    mrktTotAmt: number;
}
type CustomFeed = {foo: string};
type CustomItem = {bar: number};

const parser: Parser<CustomFeed, CustomItem> = new Parser({
  customFields: {
    feed: ['foo', 'baz'],
    //            ^ will error because `baz` is not a key of CustomFeed
    item: ['bar']
  }
});

function Stocks() {
    const [stocks, setStocks] = useState<IStocks[]>([]);
    const [loading, setLoading] = useState(true);
    useEffect(() => {
        // (async () => {
        //     const response = await fetch("https://api.odcloud.kr/api/GetStockSecuritiesInfoService/v1/getStockPriceInfo?numOfRows=100&serviceKey=GU%2FPw6QXvxX8rdXGsY9vDQWG3v8mr8imsKIAHd11KmEe0GOfu5NFoK4bv96cfhkwLKdy85VXG1mtXprIuY2HAw%3D%3D");
        //     //const json = await response.json();
        //     console.log(response)

        //     //setStocks(json);
        //     setLoading(false);
        // })();
        (async () => {

            const feed = await parser.parseURL('https://www.reddit.com/.rss');
            console.log(feed.title); // feed will have a `foo` property, type as a string

            feed.items.forEach(item => {
              console.log(item.title + ':' + item.link) // item will have a `bar` property type as a number
            });
          })();
    }, [])
    return (
        <Container>
            <Header>
                <Title>주시</Title>
            </Header>
            {loading ? (
                <Loader>Load~~~</Loader>
            ) : (
                <StockList>
                    {stocks.map((stock) => (
                        <Stock key={stock.isinCd}>
                            <Link to={`/${stock.isinCd}`}>
                            {stock.itmsNm} &rarr;
                            </Link>
                        </Stock>
                    ))}
                </StockList>
            )}
        </Container>
    )
}

export default Stocks
