import { motion } from 'framer-motion';
import React from 'react';
import { useQuery } from 'react-query';
import styled from 'styled-components';
import { getMovies, IGetMoviesResult } from '../../../api';
import { makeImagePath } from '../../../util';
import { FaChevronCircleLeft, FaChevronCircleRight, FaChevronLeft } from 'react-icons/fa'

const Wrapper = styled.div`
    width: 100%;
`

const Loader = styled.div`
    height: 20vh;
    display: flex;
    justify-content: center;
`

const Banner = styled.div<{bgPhoto:string}>`
    height: 100vh;
    display: flex;
    background-image: url( ${(props) => props.bgPhoto} );
    background-size: cover;
`

const Slider = styled.div`
    position: relative;
`
const SliderRow = styled(motion.div)`
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 1rem;
`
const SliderBox = styled(motion.div)`
    flex: 1;
    background-color: black;
    height: calc((100vw - 3.2rem - (1rem*3)) / 4 * 9/16);
`
const SliderTitle = styled.h3`
    font-size: 3rem;
    font-weight: bold;
`
const SliderArrowBox = styled.div``
const SliderArrow = styled.span``

function Home() {
    const { data, isLoading } = useQuery<IGetMoviesResult>(["movies", "nowPlaying"], getMovies)
    console.log(data, isLoading);

  return (
      <Wrapper>{isLoading ? 
        (<Loader>Loading...</Loader>) : 
        <>
            <Banner bgPhoto={makeImagePath(data?.results[0].backdrop_path || "")}>Banner</Banner>
            <Slider>
                <SliderTitle>Update</SliderTitle>
                <SliderArrowBox><FaChevronCircleLeft /><FaChevronCircleRight /></SliderArrowBox>
                <SliderRow>
                    {[1,2,3,4,5,6].map(i => 
                        <SliderBox key={i}>{i}</SliderBox>
                    )}
                </SliderRow>
            </Slider>
        </>
        }</Wrapper>
  );
}

export default Home;
