import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { useForm } from 'react-hook-form';
import { device } from '../../utils/Size';
import axios from 'axios'
import { IRegister } from '../../../api';

const RegisterWrap = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: auto;
  margin: 2rem auto auto;
  width: 100%;

  @media ${device.tablet}{
    width: 40rem;
  }
`
const RegisterHead = styled.h3`
  font-size: 3rem;
  font-weight: bold;
  font-family: 'Roboto', sans-serif;
  margin-bottom: 2rem;
`
const RegisterForm = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: flex-start;
  gap: .4rem;
  width: 100%;
`
const RegisterInput = styled.input`
  width: 100%;
  height: 4.2rem;
  padding: 1rem 1rem;
  box-sizing: border-box;
  border-radius: 0.4rem;
  border: 0.1rem solid #ccc;
  font-size: 1.6rem;
`
const RegisterTextarea = styled.textarea`
  width: 100%;
  height: 8rem;
  padding: .5rem .5rem;
  box-sizing: border-box;
  border-radius: 0.4rem;
  border: 0.1rem solid #ccc;
  font-size: 1.6rem;
  resize: none;
`
const RegisterErr = styled.span`
  color: red;
  margin-bottom: 1rem;
`
const RegisterField = styled.span`
  font-weight: 300;
  font-size: 1.4rem;
  span{
    color: ${props => props.theme.logo.pink};
  }
`
const RegisterButton = styled(motion.button)`
  width: 100%;
  height: 4.2rem;
  background-color: ${(props) => props.theme.logo.yellow};
  color: #fff;
  font-size: 2rem;
  padding: 1rem 5rem;
  box-sizing: border-box;
  border-radius: .4rem;
  //border: 1px solid ${props => props.theme.logo.pink};
`

const btnRegisterVar = {
  hover: { color: "#fff", backgroundColor: "#d3599c", transition: {duration: .3 } }
}

function RegisterPage() {
  const { register, handleSubmit, formState: { errors } } = useForm<IRegister>();

  useEffect(() => {
    axios.get('/api/hello')
    .then(response => {console.log(response);
    })
  }, [])

  return (
    <RegisterWrap>
      <RegisterHead>Register</RegisterHead>
      <RegisterForm>
        <RegisterField>Required fields<span>(â˜…)</span></RegisterField>
        <RegisterInput placeholder='Email' {...register("email", {required: "Email is required", pattern:{value: /^[a-zA-Z0-9+-_.]+@[a-zA-Z0-9-]+\.[a-zA-Z0-9-.]+$/, message: "Not Email" }})} />
        <RegisterErr>{errors?.email?.message}</RegisterErr>
        <RegisterInput placeholder='Nick' {...register("nick")} />
        <RegisterErr></RegisterErr>
        <RegisterInput placeholder='Password' />
        <RegisterErr></RegisterErr>
        <RegisterInput placeholder='Confirm Password' />
        <RegisterErr></RegisterErr>
        <RegisterField>Optional fields</RegisterField>
        <RegisterInput className='mgb12' placeholder='Name' />
        <RegisterTextarea className='mgb12' placeholder='Introduce Yourself' />
        <RegisterInput className='mgb12' placeholder='Profile Picture(Coming Soon)' />
        <RegisterButton variants={btnRegisterVar} whileHover="hover" >Sign Up</RegisterButton>
      </RegisterForm>
    </RegisterWrap>
  );
}

export default RegisterPage;
