import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion';

const LeftMenu = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 1rem;
    a{
        font-size: 2rem;
        font-weight: 500;
        padding: 1.6rem 0.5rem;
        position: relative;
        span.nav-stick{
            width: calc(100% - 1rem);
            height: .2rem;
            display: block;
            background-color: #181818;
            position: absolute;
            bottom: 0;
            left: 0.5rem;
            box-sizing: border-box;
            opacity: 0;
        }
        &:hover {
            color: ${props => props.theme.logo.pink};
            span.nav-stick{
                background-color: ${props => props.theme.logo.pink};
                opacity: .5;
            }
        }
        &#onNav{
            color: ${props => props.theme.logo.pink};
            span.nav-stick{
                background-color: ${props => props.theme.logo.pink};
                opacity: 1;
            }
        }
    }
`
const NavStick = styled(motion.span)`
    width: calc(100% - 1rem);
    height: 100%;
    display: block;
    background-color: transparent;
    border-bottom: 2px solid #181818;
    position: absolute;
    bottom: 0;
    left: 0.5rem;
    box-sizing: border-box;
    opacity: 1;
`

function LeftSide() {
  return (
      <LeftMenu>{/*  id='onNav' 현재 페이지 */}
          <Link to={`/gallery`}>Gallery<NavStick whileHover={{borderBottom: "2px solid green"}} /></Link>
          <Link to={`/health`}>Health<span className='nav-stick'></span></Link>
          <Link to={`/goods`}>Goods<span className='nav-stick'></span></Link>
          <Link to={`/together`}>Together<span className='nav-stick'></span></Link>
          <Link to={`/board`}>Board<span className='nav-stick'></span></Link>
      </LeftMenu>
  );
}

export default LeftSide;
