import { AnimatePresence, motion } from 'framer-motion';
import React, { useState } from 'react';
import { useQuery } from 'react-query';
import styled from 'styled-components';
import { getMovies, IGetMoviesResult } from '../../../api';
import { makeImagePath } from '../../../util';
import { FaChevronCircleLeft, FaChevronCircleRight } from 'react-icons/fa'

export const Slider = styled.div`
    position: relative;
    margin-top: 2.4rem;
    padding-bottom: calc((100vw - 3.2rem - (1rem*3)) / 4 * 9/16);
    overflow: hidden;
`
export const SliderRow = styled(motion.div)`
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    position: absolute;
    width: 100%;
`
export const SliderBox = styled(motion.div)<{bgPhoto:string}>`
    flex: 1 1 calc((100vw - 3.2rem) / 4);
    background-color: black;
    background-image: url(${(props) => props.bgPhoto});
    background-size: cover;
    background-position: center center;
    height: calc((100vw - 3.2rem - (1rem*3)) / 4 * 9/16);
`
export const SliderTitle = styled.h3`
    font-size: 3rem;
    font-weight: bold;
`
export const SliderArrowBox = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 1rem;
    position: absolute;
    top: 0;
    right: 0;
    height: 3rem;
    font-size: 2rem;
    color: ${(props) => props.theme.logo.bk};
`

function HomeCommon() {
  return <div>Home Common</div>;
}

export default HomeCommon;
