import axios from 'axios';

const BASE_USER = `api/users/`;

const API_KEY = "4de9f22b35bd87922db9fa7e7348fb77";
const BASE_PATH = "https://api.themoviedb.org/3";

interface IMovie {
    id: number;
    backdrop_path: string;
    title: string;
    poster_path: string;
    overview: string;
}

export interface IGetMoviesResult {
    dates: {
        maximum: string;
        minimum: string;
    };
    page: number;
    results: IMovie[];
    total_pages: number;
    total_results: number;
}

export function getMovies (){
    return fetch(`${BASE_PATH}/movie/now_playing?api_key=${API_KEY}`).then((response) => response.json())
}


export interface ITableSize {
    winSize: boolean;
}

interface ILogin {
    email: string;
    password: string;
}

export function fetchLogin(loginData:ILogin) {
    axios.post(`${BASE_USER}/login`, loginData).then(response => console.log(response))
}
