import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import styled from 'styled-components';
import { useForm } from 'react-hook-form';
import { device } from '../../utils/Size';
import axios from 'axios'
import { IRegister } from '../../../api';

const RegisterWrap = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: calc((100vh - 5.6rem)/2);
  margin: 0 auto;
  width: 100%;

  @media ${device.tablet}{
    width: 40rem;
  }
`
const RegisterHead = styled.h3`
  font-size: 3rem;
  font-weight: bold;
  font-family: 'Roboto', sans-serif;
  margin-bottom: 2rem;
`
const RegisterForm = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: flex-start;
  gap: .4rem;
  width: 100%;
`
const RegisterInput = styled.input`
  width: 100%;
  height: 4.2rem;
  padding: 1rem 1rem;
  box-sizing: border-box;
  border-radius: 0.4rem;
  border: 0.1rem solid #ccc;
  font-size: 1.6rem;
`
const RegisterTextarea = styled.textarea`
  width: 100%;
  height: 8rem;
  padding: .5rem .5rem;
  box-sizing: border-box;
  border-radius: 0.4rem;
  border: 0.1rem solid #ccc;
  font-size: 1.6rem;
  resize: none;
`
const RegisterErr = styled.span`
  color: red;
  margin-bottom: 1rem;
`
const RegisterField = styled.span`
  font-weight: 100;
  font-size: 1.4rem;
`


function RegisterPage() {
  const { register, handleSubmit, formState: { errors } } = useForm<IRegister>();

  useEffect(() => {
    axios.get('/api/hello')
    .then(response => {console.log(response);
    })
  }, [])

  return (
    <RegisterWrap>
      <RegisterHead>Register</RegisterHead>
      <RegisterForm>
        <RegisterField>Required fields</RegisterField>
        <RegisterInput placeholder='Email' />
        <RegisterErr></RegisterErr>
        <RegisterInput placeholder='Nick' />
        <RegisterErr></RegisterErr>
        <RegisterInput placeholder='Password' />
        <RegisterErr></RegisterErr>
        <RegisterInput placeholder='Confirm Password' />
        <RegisterErr></RegisterErr>
        <RegisterField>Optional fields</RegisterField>
        <RegisterInput className='mgb12' placeholder='Name' />
        <RegisterTextarea className='mgb12' placeholder='Introduce Yourself' />
        <RegisterInput placeholder='Profile Picture(Coming Soon)' />
      </RegisterForm>
    </RegisterWrap>
  );
}

export default RegisterPage;
