import React, { useState } from 'react';
import styled from 'styled-components';
import { Link, useRouteMatch } from 'react-router-dom';
import { FaSearch } from 'react-icons/fa';
import { motion } from 'framer-motion';

const RightMenu = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 1rem;

    a{
        font-size: 1.6rem;
        font-weight: 300;
        padding: 1.8rem 0.5rem;
        position: relative;
        height: 5.6rem;
        &:hover {
            color: ${props => props.theme.logo.pink}
        }
        &#onNav{
            color: ${props => props.theme.logo.pink};
            span{
                border-bottom: 2px solid ${props => props.theme.logo.pink};
                opacity: 1;
            }
        }
    }
`
const NavStick = styled(motion.span)`
    width: calc(100% - 1rem);
    height: 100%;
    display: block;
    background-color: transparent;
    border-bottom: 2px solid #181818;
    position: absolute;
    bottom: 0;
    left: 0.5rem;
    box-sizing: border-box;
    opacity: 0;
`
const NavStickVars = {
    hover: { borderBottom: `2px solid #d3599c`, opacity: 1 }
}
const SearchContainer = styled.span`
    position: relative;
    display: flex;
    justify-content: center;
    align-items: center;
`
const SearchIcon = styled(motion.span)`
    font-size: 1.8rem;
    padding: 1.6rem 0.5rem;
    &:hover {
        color: ${props => props.theme.logo.pink};
    }
`
const SearchInput = styled(motion.input)`
    transform-origin: right;
    position: absolute;
    right: 0;
    width: 15rem;
`

function RightSide() {
    const signinMatch = useRouteMatch("/signin");
    const [searchOpen, setSearchOpen] = useState(false);
    const toggleSearch = () => setSearchOpen(prev => !prev)

  return (
    <RightMenu>
        <SearchContainer>
            <SearchIcon
                onClick={toggleSearch}
                animate={{ x: searchOpen ? -180 : 0  }}
                transition={{ type: "linear" }}
            >
                <FaSearch />
            </SearchIcon>
            <SearchInput
            transition={{ type: "linear" }}
            animate={{ scaleX: searchOpen ? 1 : 0 }}
            placeholder="Search for movie or tv show..." 
            />
        </SearchContainer>
        <Link to={`/signin`}>Sign In{signinMatch && <NavStick whileHover="hover" variants={NavStickVars} />}</Link>
    </RightMenu>
  );
}

export default RightSide;
