import React from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { FaSearch } from 'react-icons/fa';
import { motion } from 'framer-motion';

const RightMenu = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 1rem;

    a{
        font-size: 2rem;
        font-weight: 500;
        padding: 1.6rem 0.5rem;
        &:hover {
            color: ${props => props.theme.logo.pink}
        }
        &#onNav{
            color: ${props => props.theme.logo.pink};
            span{
                border-bottom: 2px solid ${props => props.theme.logo.pink};
                opacity: 1;
            }
        }
    }
`
const NavStick = styled(motion.span)`
    width: calc(100% - 1rem);
    height: 100%;
    display: block;
    background-color: transparent;
    border-bottom: 2px solid #181818;
    position: absolute;
    bottom: 0;
    left: 0.5rem;
    box-sizing: border-box;
    opacity: 0;
`
const NavStickVars = {
    hover: { borderBottom: `2px solid #d3599c`, opacity: 1 }
}
const ButtonSearch = styled.button`
    font-size: 1.8rem;
    padding: 1.6rem 0.5rem;
    &:hover {
        color: ${props => props.theme.logo.pink};
    }
`

function RightSide() {
  return (
    <RightMenu>
        <ButtonSearch><FaSearch /></ButtonSearch>
        <Link to={`/signin`}>Sign In<NavStick whileHover="hover" variants={NavStickVars} /></Link>
        {/* <Link to={`/profile`}>Profile</Link> */}
    </RightMenu>
  );
}

export default RightSide;
