import React from 'react';
import styled from 'styled-components';
import { Link, useRouteMatch } from 'react-router-dom';
import { motion } from 'framer-motion';

const LeftMenu = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 1rem;
    a{
        font-size: 2rem;
        font-weight: 500;
        padding: 1.6rem 0.5rem;
        position: relative;
        &:hover {
            color: ${props => props.theme.logo.pink};
        }
        &#onNav{
            color: ${props => props.theme.logo.pink};
            span{
                border-bottom: 2px solid ${props => props.theme.logo.pink};
                opacity: 1;
            }
        }
    }
`
const NavStick = styled(motion.span)`
    width: calc(100% - 1rem);
    height: 100%;
    display: block;
    background-color: transparent;
    border-bottom: 2px solid #181818;
    position: absolute;
    bottom: 0;
    left: 0.5rem;
    box-sizing: border-box;
    opacity: 0;
`
const NavStickVars = {
    hover: { borderBottom: `2px solid #d3599c`, opacity: 1 }
}

function LeftSide() {
    const homeMatch = useRouteMatch("/");
    const galleryMatch = useRouteMatch("/gallery");
    const healthMatch = useRouteMatch("/health");
    const goodsMatch = useRouteMatch("/goods");
    const togetherMatch = useRouteMatch("/together");
    const boardMatch = useRouteMatch("/board");
  return (
      <LeftMenu>{/*  id='onNav' 현재 페이지 */}
          <Link to={`/gallery`}>Gallery<NavStick whileHover="hover" variants={NavStickVars} /></Link>
          <Link to={`/health`}>Health<NavStick whileHover="hover" variants={NavStickVars} /></Link>
          <Link to={`/goods`}>Goods<NavStick whileHover="hover" variants={NavStickVars} /></Link>
          <Link to={`/together`}>Together<NavStick whileHover="hover" variants={NavStickVars} /></Link>
          <Link to={`/board`}>Board<NavStick whileHover="hover" variants={NavStickVars} /></Link>
      </LeftMenu>
  );
}

export default LeftSide;
