import React from 'react';
import { useForm } from 'react-hook-form';
import styled from 'styled-components';
import { device } from '../../utils/Size';

const LoginWrap = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: calc((100vh - 5.6rem)/2);
`
const LoginForm = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 1rem;
`
const LoginInput = styled.input`
  width: 100%;
  padding: 0.5rem 1rem;
  box-sizing: border-box;
  border-radius: 0.4rem;
  border: 0.1rem solid ${props => props.theme.dark.lighter};
  font-size: 1.6rem;

  @media ${device.tablet} {
    width: auto;
  }
`
const LoginButton = styled.button`
  width: 100%;
  background-color: ${(props) => props.theme.logo.pink};

  @media ${device.tablet} {
    width: 100%;
  }
`
const OtherBox = styled.div``
const AccountForgot = styled.a``
const RegisterButton = styled.button``

function LoginPage() {
  const { register } = useForm();
  return (
    <LoginWrap>
      <LoginForm>
        <LoginInput {...register("email")} placeholder='Email' />
        <LoginInput {...register("password")} placeholder='Password' />
        <LoginButton>Sign In</LoginButton>
      </LoginForm>
      <OtherBox>
        <AccountForgot>Forgot my password</AccountForgot>
        <RegisterButton {...register("Register")}>Sign Up</RegisterButton>
      </OtherBox>
    </LoginWrap>
  );
}

export default LoginPage;
