import { AnimatePresence, motion } from 'framer-motion';
import React, { useState } from 'react';
import { useQuery } from 'react-query';
import styled from 'styled-components';
import { getMovies, IGetMoviesResult } from '../../../api';
import { makeImagePath } from '../../../util';
import { FaChevronCircleLeft, FaChevronCircleRight } from 'react-icons/fa'

const Wrapper = styled.div`
    width: 100%;
`

const Loader = styled.div`
    height: 20vh;
    display: flex;
    justify-content: center;
`

const Banner = styled.div<{bgPhoto:string}>`
    height: 100vh;
    display: flex;
    background-image: url( ${(props) => props.bgPhoto} );
    background-size: cover;
`

const Slider = styled.div`
    position: relative;
    margin-top: 2.4rem;
    padding-bottom: calc((100vw - 3.2rem - (1rem*3)) / 4 * 9/16);
    overflow: hidden;
`
const SliderRow = styled(motion.div)`
    display: flex;
    justify-content: center;
    align-items: center;
    gap: 1rem;
    position: absolute;
    width: 100%;
`
const SliderBox = styled(motion.div)<{bgPhoto:string}>`
    flex: 1 1 calc((100vw - 3.2rem) / 4);
    background-color: black;
    background-image: url(${(props) => props.bgPhoto});
    background-size: cover;
    background-position: center center;
    height: calc((100vw - 3.2rem - (1rem*3)) / 4 * 9/16);
`
const SliderTitle = styled.h3`
    font-size: 3rem;
    font-weight: bold;
`
const SliderArrowBox = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    gap: 1rem;
    position: absolute;
    top: 0;
    right: 0;
    height: 3rem;
    font-size: 2rem;
    color: ${(props) => props.theme.logo.bk};
`
const MiddleLine = styled.div`
    width: 100%;
    background-color: #ccc;
    padding-top: .1rem;
    box-sizing: border-box;
    border-top: 2.4rem solid ${props => props.theme.light.darker};
    border-bottom: 2.4rem solid ${props => props.theme.light.darker};
`

const sliderVariants = {
    hidden: (direction:boolean) => ({
        x: direction? -window.outerWidth + 20 : window.outerWidth - 20
    }),
    visible: {
        x: 0
    },
    exit: (direction:boolean) => ({
        x: direction? window.outerWidth - 20 : -window.outerWidth + 20
    })
}
const sliderOffset = 4;

function Home() {
    const { data, isLoading } = useQuery<IGetMoviesResult>(["movies", "nowPlaying"], getMovies)
    const [recentIndex, setRecentIndex] = useState(0);
    const [sliderDirection, setSliderDirection] = useState(false);
    const [leaving, setLeaving] = useState(false);
    const increaseRecent = () => {
        if(data){
            if(leaving) return;
            toggleLeaving();
            setSliderDirection(false);
            const maxDataIndex = Math.ceil(data.results.length / sliderOffset) -1
            setRecentIndex((prev) => ( prev===maxDataIndex? 0 : prev+1));
        }
    }
    const decreaseRecent = () => {
        if(data){
            if(leaving) return;
            toggleLeaving();
            setSliderDirection(true);
            const maxDataIndex = Math.ceil(data.results.length / sliderOffset) -1
            setRecentIndex((prev) => ( prev===0 ? maxDataIndex : prev -1));
        }
    }
    const toggleLeaving =() => setLeaving((prev) => !prev);

  return (
      <Wrapper>{isLoading ? 
        (<Loader>Loading...</Loader>) : 
        <>
            {/* <Banner bgPhoto={makeImagePath(data?.results[0].backdrop_path || "")}>Banner</Banner> */}
            <Slider>
                <SliderTitle>Recent Gallery</SliderTitle>
                <SliderArrowBox>
                    <FaChevronCircleLeft onClick={decreaseRecent} /><FaChevronCircleRight onClick={increaseRecent} />
                </SliderArrowBox>
                <AnimatePresence 
                    initial={false} 
                    custom={sliderDirection}
                    onExitComplete={toggleLeaving}
                    >
                    <SliderRow 
                        custom={sliderDirection}
                        variants={sliderVariants}
                        initial="hidden"
                        animate="visible"
                        exit="exit"
                        transition={{ type: "tween", duration: 1 }}
                        key={recentIndex}
                    >
                        {data?.results.slice(sliderOffset * recentIndex, sliderOffset * recentIndex + sliderOffset).map((movie) => (
                            <SliderBox key={movie.id} bgPhoto={makeImagePath(movie.backdrop_path, "w500")}></SliderBox>
                        ))}
                    </SliderRow>
                </AnimatePresence>
            </Slider>
            <MiddleLine />

        </>
        }</Wrapper>
  );
}

export default Home;
