import React from 'react';
import styled from 'styled-components';
import { Link, useRouteMatch } from 'react-router-dom';
import { motion } from 'framer-motion';

const LeftMenu = styled.div`
    display: flex;
    align-items: center;
    justify-content: space-between;
    gap: 1rem;
    a{
        font-size: 2rem;
        font-weight: 500;
        padding: 1.6rem 0.5rem;
        position: relative;
        &:hover {
            color: ${props => props.theme.logo.pink};
        }
    }
`
const NavStick = styled(motion.span)`
    width: calc(100% - 1rem);
    height: 100%;
    display: block;
    background-color: transparent;
    border-bottom: 3px solid #181818;
    position: absolute;
    bottom: 0;
    left: 0.5rem;
    box-sizing: border-box;
    opacity: 1;
`
const NavStickVars = {
    hover: { borderBottom: `3px solid #d3599c`, opacity: 1 }
}

function LeftSide() {
    const homeMatch = useRouteMatch("/");
    const galleryMatch = useRouteMatch("/gallery");
    const healthMatch = useRouteMatch("/health");
    const goodsMatch = useRouteMatch("/goods");
    const togetherMatch = useRouteMatch("/together");
    const boardMatch = useRouteMatch("/board");

    console.log(homeMatch, galleryMatch);

  return (
      <LeftMenu>
          <Link to={`/gallery`}>Gallery{galleryMatch && <NavStick layoutId='stick' whileHover="hover" variants={NavStickVars} />}</Link>
          <Link to={`/health`}>Health{ healthMatch && <NavStick layoutId='stick' whileHover="hover" variants={NavStickVars} />}</Link>
          <Link to={`/goods`}>Goods{goodsMatch && <NavStick layoutId='stick' whileHover="hover" variants={NavStickVars} />}</Link>
          <Link to={`/together`}>Together{togetherMatch && <NavStick layoutId='stick' whileHover="hover" variants={NavStickVars} />}</Link>
          <Link to={`/board`}>Board{boardMatch && <NavStick layoutId='stick' whileHover="hover" variants={NavStickVars} />}</Link>
      </LeftMenu>
  );
}

export default LeftSide;
