import { motion } from 'framer-motion';
import React from 'react';
import { useForm } from 'react-hook-form';
import styled from 'styled-components';
import { device } from '../../utils/Size';

const LoginWrap = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: calc((100vh - 5.6rem)/2);
`
const LoginForm = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  gap: 1.4rem;

  @media ${device.tablet}{
    width: 40rem;
  }
`
const LoginInput = styled.input`
  width: 100%;
  height: 4.2rem;
  padding: 1rem 1rem;
  box-sizing: border-box;
  border-radius: 0.4rem;
  border: 0.1rem solid #ccc;
  font-size: 1.6rem;
`
const LoginButton = styled(motion.button)`
  width: 100%;
  height: 4.2rem;
  background-color: ${(props) => props.theme.logo.pink};
  font-size: 2rem;
  padding: 1rem 5rem;
  box-sizing: border-box;
  border-radius: .4rem;
  //border: 1px solid ${props => props.theme.logo.pink};
`
const OtherBox = styled.div``
const AccountForgot = styled.a``
const RegisterButton = styled.button``

const btnLoginVar = {
  hover: { color: "#fff", backgroundColor: "#000", transition: {duration: .3 } }
}

function LoginPage() {
  const { register } = useForm();
  return (
    <LoginWrap>
      <LoginForm>
        <LoginInput {...register("email")} placeholder='Email' />
        <LoginInput {...register("password")} placeholder='Password' />
        <LoginButton variants={btnLoginVar} whileHover="hover" >Sign In</LoginButton>
      </LoginForm>
      <OtherBox>
        <AccountForgot>Forgot my password</AccountForgot>
        <RegisterButton {...register("Register")}>Sign Up</RegisterButton>
      </OtherBox>
    </LoginWrap>
  );
}

export default LoginPage;
