import { motion } from 'framer-motion';
import React from 'react';
import { useForm } from 'react-hook-form';
import styled from 'styled-components';
import { device } from '../../utils/Size';

const LoginWrap = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  height: calc((100vh - 5.6rem)/2);
  margin: 0 auto;
  width: 100%;

  @media ${device.tablet}{
    width: 40rem;
  }
`
const LoginForm = styled.form`
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  align-items: flex-start;
  gap: 1.4rem;
  width: 100%;
`
const LoginInput = styled.input`
  width: 100%;
  height: 4.2rem;
  padding: 1rem 1rem;
  box-sizing: border-box;
  border-radius: 0.4rem;
  border: 0.1rem solid #ccc;
  font-size: 1.6rem;
`
const LoginButton = styled(motion.button)`
  width: 100%;
  height: 4.2rem;
  background-color: ${(props) => props.theme.logo.brown};
  color: #fff;
  font-size: 2rem;
  padding: 1rem 5rem;
  box-sizing: border-box;
  border-radius: .4rem;
  //border: 1px solid ${props => props.theme.logo.pink};
`
const OtherBox = styled.div`
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-top: 1rem;
`
const AccountForgot = styled.a`
  text-decoration: underline;
  text-underline-position: under;
`
const RegisterButton = styled(motion.button)`
  padding: 0.8rem 2rem;
  background-color: #e0bd2b;
  color: #fff;
  border-radius: 0.4rem;
  font-size: 1.6rem;
`

const btnLoginVar = {
  hover: { color: "#fff", backgroundColor: "#d3599c", transition: {duration: .3 } }
}
const btnRegisterVar = {
  hover: { color: "#fff", backgroundColor: "#d3599c", transition: {duration: .3 } }
}

const registerClick = () => {
  window.location.href = `/signup`
}

interface Iform {
  email: string;
  password: string;
}

function LoginPage() {
  const { register, handleSubmit, formState: { errors } } = useForm<Iform>();
  const onVal = (data: Iform) => {
    console.log(data);
  }

  return (
    <LoginWrap>
      <LoginForm onSubmit={handleSubmit(onVal)}>
        <LoginInput {...register("email", {required:"Email is required"})} placeholder='Email' />
        <LoginInput {...register("password", {required:"Password is required"})} placeholder='Password' />
        <label htmlFor="rememberMe">
        <input type="checkbox" id="rememberMe" />Remember Me</label>
        <LoginButton variants={btnLoginVar} whileHover="hover" >Sign In</LoginButton>
      </LoginForm>
      <OtherBox>
        <AccountForgot href='/forgot' >Forgot my password</AccountForgot>
        <RegisterButton variants={btnRegisterVar} whileHover='hover' onClick={registerClick}>Sign Up</RegisterButton>
      </OtherBox>
    </LoginWrap>
  );
}

export default LoginPage;
